$(".qa-card__show").click(function () {
    $(this).parents(".qa-card").toggleClass("_active")
    $(this).siblings(".qa-card__hidden").slideToggle()
})

let burger = document.querySelector(".burger")
let body = document.querySelector("body")
let header = document.querySelector(".header")
let menuClose = document.querySelector(".menu__bg")
let menuLinks = document.querySelectorAll(".menu__link")


if (burger) {
    burger.onclick = function() {
        header.classList.toggle("_open")
        body.classList.toggle("fixed-body")
    }

    document.addEventListener( 'click', (e) => {
        const withinBoundaries = e.composedPath().includes(header);

        if ( ! withinBoundaries ) {
            closeMenu()
        }
    })
}

if(menuClose){
    menuClose.onclick = function () {
        closeMenu()
    }
}

function closeMenu(){
    header.classList.remove("_open")
    body.classList.remove("fixed-body")
}

menuLinks.forEach(link => {
    link.onclick = function () {
        closeMenu()
    }
})



$(".header-feedback").hover(onIn, onOut);

function onIn() {
    if (window.innerWidth > 992) {
        let el = $(this)
        setTimeout(function() {
            if (el.is(':hover')) {
                el.addClass("_show")
            }
        }, 200);
    }
}

function onOut() {
    if (window.innerWidth > 992) {
        $(this).removeClass("_show")
    }
}

$(".header-feedback__btn").click(function () {
    if (window.innerWidth <= 992){
        $(this).parents(".header-feedback").toggleClass("_show")
    }
})


$(document).mouseup( function(e){
    var div = $( ".header-feedback" );
    if ( !div.is(e.target)
        && div.has(e.target).length === 0 ) {
        if($(".header-feedback").hasClass("_show")){
            $(".header-feedback").removeClass("_show")
        }
    }
});

function Sliders() {
    let SlidersArray = []

    let sliders = document.querySelectorAll("._slider")
    sliders.forEach((slider,index) => {
        slider.setAttribute("data-slider-id",index)

        SlidersArray[index] = new Swiper(`._slider[data-slider-id="${index}"] .swiper-container`, {
            slidesPerView: "auto",
            spaceBetween: 0,
            watchSlidesProgress: true,
            preventClicks :true,
            a11y: false,
            observer: true,
            observeParents: true,
            observeSlideChildren: true,
            navigation: {
                nextEl: `._slider[data-slider-id="${index}"] .swiper-button-next`,
                prevEl: `._slider[data-slider-id="${index}"] .swiper-button-prev`,
            },
            pagination: {
                el: `._slider[data-slider-id="${index}"] .swiper-pagination`,
                type: 'bullets',
                clickable: true,
            },
        })
    })
}

Sliders()

let modalTogglers = document.querySelectorAll("[data-modal]")
let modals = document.querySelectorAll(".modal")

modalTogglers.forEach(el => {
    el.onclick = function () {
        let modalId = el.getAttribute("data-modal")
        openModal(modalId)
    }
})

function closeModals() {
    modals.forEach(modal => {
        modal.classList.remove("_active")
        body.classList.remove("fixed-body2")
    })
}

function openModal(modalId) {
    closeModals()
    let modal = document.querySelector(`#${modalId}`)
    let closeButtons = modal.querySelectorAll(`._close-modal`)

    modal.classList.add("_active")
    body.classList.add("fixed-body2")


    closeButtons.forEach(btn => {
        btn.onclick = ()=> {
            modal.classList.remove("_active")
            body.classList.remove("fixed-body2")
        }
    })
}

